function run(APIResponse) {
    // Parse the JSON response
    var data = JSON.parse(APIResponse);

    // Extract the name of the first repository using regex
    var nameRegex = /"name":"([^"]+)"/;
    var nameMatch = nameRegex.exec(JSON.stringify(data[0]));
    var primeiroTitulo = nameMatch ? nameMatch[1] : "";

    // Return only the value of primeiroTitulo
    return primeiroTitulo;
}

